import React, { useState, useRef, useEffect } from 'react';
import VideoSearchForm from './components/VideoSearchForm';
import SnippetEditor from './components/SnippetEditor';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

// A simple full-screen overlay spinner.
function LoadingOverlay() {
  return (
    <div className="loading-overlay">
      <div className="spinner-border text-light" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    </div>
  );
}

function App() {
  // Welcome and Terms state
  const [showWelcome, setShowWelcome] = useState(true);
  const [acceptedTerms, setAcceptedTerms] = useState(false);

  const [youtubeUrl, setYoutubeUrl] = useState('');
  const [chunks, setChunks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [snippets, setSnippets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [videoPath, setVideoPath] = useState(null);
  const [error, setError] = useState(null);

  // Track the current step.
  // 1 - Fetch Transcript
  // 2 - Search Transcript
  // 3 - Refine Snippets
  // 4 - Create Video
  // 5 - Video (Preview/Download)
  const [currentStep, setCurrentStep] = useState(1);

  // Refs for auto-focusing the next action button/section.
  const searchButtonRef = useRef(null);
  const refineButtonRef = useRef(null);
  const createVideoButtonRef = useRef(null);
  const downloadButtonRef = useRef(null);

  // Focus logic: whenever currentStep changes AND we're not loading, focus the next button.
  useEffect(() => {
    if (!loading) {
      switch (currentStep) {
        case 2:
          searchButtonRef.current?.focus();
          break;
        case 3:
          refineButtonRef.current?.focus();
          break;
        case 4:
          createVideoButtonRef.current?.focus();
          break;
        case 5:
          downloadButtonRef.current?.focus();
          break;
        default:
          break;
      }
    }
  }, [currentStep, loading]);

  // Called when the user clicks Start on the welcome card.
  const handleStart = () => {
    if (acceptedTerms) {
      setShowWelcome(false);
      setCurrentStep(1);
    }
  };

  const handleFetchChunks = async (url) => {
    try {
      setYoutubeUrl(url);
      setLoading(true);
      setError(null);
      setSnippets([]);
      setVideoPath(null);

      const response = await axios.post('http://localhost:5001/api/transcript-chunks', {
        youtubeUrl: url,
        maxChunkSize: 200,
      });
      if (response.data.error) {
        setError(response.data.error);
      } else {
        setChunks(response.data.chunks);
        toast.success('Transcript chunks fetched successfully!');
        setCurrentStep(2); // Move to Step 2: Search Transcript
      }
    } catch (err) {
      toast.error('Failed to fetch transcript chunks.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (query, topK = 5) => {
    if (!chunks.length) return;
    try {
      setSearchQuery(query);
      setLoading(true);
      setError(null);
      setVideoPath(null);

      const resp = await axios.post('http://localhost:5001/api/search-snippets', {
        chunks: chunks,
        query: query,
        topK: topK,
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setSnippets(resp.data.results);
        toast.success('Top snippets found!');
        setCurrentStep(3); // Move to Step 3: Refine Snippets
      }
    } catch (err) {
      toast.error('Snippet search failed.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleRefineOrder = async () => {
    if (!snippets.length) return;
    try {
      setLoading(true);
      setError(null);

      const resp = await axios.post('http://localhost:5001/api/refine-snippets', {
        snippets: snippets,
        query: searchQuery,
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        const newOrder = resp.data.order;
        const reordered = newOrder.map((idx) => snippets[idx]).filter(Boolean);
        setSnippets(reordered);
        toast.info('Snippets refined and reordered.');
        setCurrentStep(4); // Move to Step 4: Create Video
      }
    } catch (err) {
      toast.error('Refining order failed.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Skip refining and go directly to video creation.
  const handleSkipRefine = () => {
    setCurrentStep(4);
  };

  const handleCreateVideo = async (finalSnippets) => {
    try {
      setLoading(true);
      setError(null);
      setVideoPath(null);
      const resp = await axios.post('http://localhost:5001/api/create-video', {
        youtubeUrl: youtubeUrl,
        snippets: finalSnippets,
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setVideoPath(resp.data.videoPath);
        toast.success('Highlight video created!');
        setCurrentStep(5); // Move to Step 5: Video (Preview/Download)
      }
    } catch (err) {
      toast.error('Video creation failed.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Helper: allow navigation to any step â‰¤ currentStep.
  const isStepAccessible = (step) => step <= currentStep;

  return (
    <div className="container py-4 position-relative">
      {loading && <LoadingOverlay />}
      <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />

      {/* Welcome and Terms Card */}
      {showWelcome && (
        <div className="welcome-card">
          <div className="card p-4 text-center">
            <h2>Welcome to AI Video Editor</h2>
            <p>
              By using this service, you agree to our Terms and Conditions. Please read them carefully.
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque euismod, nisi vel consectetur
              interdum, nisl nisi aliquam nisi, euismod aliquam nisi nisi euismod nisi.
            </p>
            <div className="form-check mb-3">
              <input
                type="checkbox"
                className="form-check-input"
                id="termsCheck"
                checked={acceptedTerms}
                onChange={(e) => setAcceptedTerms(e.target.checked)}
              />
              <label className="form-check-label" htmlFor="termsCheck">
                I agree to the Terms and Conditions.
              </label>
            </div>
            <button className="btn btn-primary" disabled={!acceptedTerms} onClick={handleStart}>
              Start
            </button>
          </div>
        </div>
      )}

      {/* Main Application (only shown once welcome card is dismissed) */}
      {!showWelcome && (
        <>
          {/* Tab Navigation Header */}
          <ul className="nav nav-tabs">
            <li className="nav-item">
              <button
                className={`nav-link ${currentStep === 1 ? 'active' : ''}`}
                onClick={() => setCurrentStep(1)}
              >
                1. Fetch Transcript
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${currentStep === 2 ? 'active' : ''}`}
                onClick={() => isStepAccessible(2) && setCurrentStep(2)}
                disabled={!isStepAccessible(2)}
              >
                2. Search Transcript
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${currentStep === 3 ? 'active' : ''}`}
                onClick={() => isStepAccessible(3) && setCurrentStep(3)}
                disabled={!isStepAccessible(3)}
              >
                3. Refine Snippets
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${currentStep === 4 ? 'active' : ''}`}
                onClick={() => isStepAccessible(4) && setCurrentStep(4)}
                disabled={!isStepAccessible(4)}
              >
                4. Create Video
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${currentStep === 5 ? 'active' : ''}`}
                onClick={() => isStepAccessible(5) && setCurrentStep(5)}
                disabled={!isStepAccessible(5)}
              >
                5. Video
              </button>
            </li>
          </ul>

          {/* Tab Content */}
          <div className="tab-content mt-4">
            {currentStep === 1 && (
              <div className="tab-pane active">
                <VideoSearchForm onFetchChunks={handleFetchChunks} loading={loading} />
              </div>
            )}

            {currentStep === 2 && (
              <div className="tab-pane active">
                <div className="card my-4 p-3">
                  <h3>Step 2: Search in Transcript</h3>
                  <p>Enter a query to find relevant parts:</p>
                  <div className="input-group mb-3">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="e.g. 'social media impact'"
                      onChange={(e) => setSearchQuery(e.target.value)}
                      value={searchQuery}
                    />
                    <button
                      className="btn btn-primary"
                      ref={searchButtonRef}
                      onClick={() => handleSearch(searchQuery, 5)}
                      disabled={!searchQuery || loading}
                    >
                      Search
                    </button>
                  </div>
                </div>
              </div>
            )}

            {currentStep === 3 && (
              <div className="tab-pane active">
                <div className="card my-4 p-3">
                  <h3>Step 3: Refine Snippets</h3>
                  <p>
                    Review the snippets below. You can refine/reorder them or skip this step.
                  </p>
                  <ul>
                    {snippets.map((snippet, index) => (
                      <li key={index}>{snippet.text}</li>
                    ))}
                  </ul>
                  <div className="d-flex gap-3 mt-3">
                    <button
                      className="btn btn-secondary"
                      ref={refineButtonRef}
                      onClick={handleRefineOrder}
                      disabled={loading}
                    >
                      Refine / Reorder
                    </button>
                    <button className="btn btn-light" onClick={handleSkipRefine} disabled={loading}>
                      Skip
                    </button>
                  </div>
                </div>
              </div>
            )}

            {currentStep === 4 && (
              <div className="tab-pane active">
                <div className="card my-4 p-3">
                  <h3>Step 4: Create Video</h3>
                  <SnippetEditor
                    snippets={snippets}
                    onCreateVideo={handleCreateVideo}
                    loading={loading}
                    createVideoButtonRef={createVideoButtonRef}
                  />
                </div>
              </div>
            )}

            {currentStep === 5 && videoPath && (
              <div className="tab-pane active">
                <div className="card p-3 my-4">
                  <h3>Step 5: Video Preview</h3>
                  <div className="text-center">
                    <video
                      className="video-player"
                      controls
                      style={{ width: '100%', maxWidth: '600px', borderRadius: '8px' }}
                    >
                      <source
                        src={`http://localhost:5001/api/download-video/${videoPath.split('/').pop()}`}
                        type="video/mp4"
                      />
                      Your browser does not support the video tag.
                    </video>
                    <div className="mt-3">
                      <a
                        href={`http://localhost:5001/api/download-video/${videoPath.split('/').pop()}`}
                        className="btn btn-success"
                        target="_blank"
                        rel="noreferrer"
                        style={{ width: 'auto' }}
                        ref={downloadButtonRef}
                      >
                        Download Video
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Error Alert */}
          {error && <div className="alert alert-danger my-3">{error}</div>}
        </>
      )}
    </div>
  );
}

export default App;
