import React, { useState } from 'react';
import VideoSearchForm from './components/VideoSearchForm';
import SnippetEditor from './components/SnippetEditor';
import axios from 'axios';
import { motion } from 'framer-motion';

function App() {
  const [youtubeUrl, setYoutubeUrl] = useState('');
  const [chunks, setChunks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [snippets, setSnippets] = useState([]);
  const [refinedOrder, setRefinedOrder] = useState([]);

  const [loading, setLoading] = useState(false);
  const [videoPath, setVideoPath] = useState(null);
  const [error, setError] = useState(null);

  // 1) Fetch transcript chunks
  const handleFetchChunks = async (url) => {
    try {
      setYoutubeUrl(url);
      setLoading(true);
      setError(null);
      setSnippets([]);
      setVideoPath(null);
      const response = await axios.post('http://localhost:5001/api/transcript-chunks', {
        youtubeUrl: url,
        maxChunkSize: 200
      });
      if (response.data.error) {
        setError(response.data.error);
      } else {
        setChunks(response.data.chunks);
      }
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  // 2) Search for relevant snippets
  const handleSearch = async (query, topK=5) => {
    if (!chunks.length) return;
    try {
      setSearchQuery(query);
      setLoading(true);
      setError(null);
      setVideoPath(null);

      const resp = await axios.post('http://localhost:5001/api/search-snippets', {
        chunks: chunks,
        query: query,
        topK: topK
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setSnippets(resp.data.results);
        setRefinedOrder([]);
      }
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  // 3) Refine snippet order using LLM
  const handleRefineOrder = async () => {
    if (!snippets.length) return;
    try {
      setLoading(true);
      setError(null);

      const resp = await axios.post('http://localhost:5001/api/refine-snippets', {
        snippets: snippets,
        query: searchQuery
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        const newOrder = resp.data.order;
        const reordered = newOrder.map(idx => snippets[idx]).filter(Boolean);
        setSnippets(reordered);
        setRefinedOrder(newOrder);
      }
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  // 4) Create final highlight video
  const handleCreateVideo = async (finalSnippets) => {
    try {
      setLoading(true);
      setError(null);
      setVideoPath(null);
      const resp = await axios.post('http://localhost:5001/api/create-video', {
        youtubeUrl: youtubeUrl,
        snippets: finalSnippets
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setVideoPath(resp.data.videoPath);
      }
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <motion.h1 className="text-3xl font-bold text-center mb-6" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        AI Video Editor
      </motion.h1>

      <motion.div initial={{ scale: 0.9, opacity: 0 }} animate={{ scale: 1, opacity: 1 }}>
        <VideoSearchForm onFetchChunks={handleFetchChunks} loading={loading} />
      </motion.div>

      {error && <div className="alert alert-danger my-3">{error}</div>}
      {loading && <motion.div className="loader my-3" animate={{ rotate: 360 }} transition={{ repeat: Infinity, duration: 1 }}></motion.div>}

      {chunks.length > 0 && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="card p-3 my-4">
          <h3 className="text-xl font-semibold mb-3">Step 2: Search in Transcript</h3>
          <p>Enter a query to find relevant parts of the transcript:</p>
          <div className="input-group my-3">
            <input
              type="text"
              className="form-control"
              placeholder="e.g. 'why social media is harmful'"
              onChange={(e) => setSearchQuery(e.target.value)}
              value={searchQuery}
            />
            <button
              className="btn btn-primary"
              onClick={() => handleSearch(searchQuery, 5)}
              disabled={!searchQuery || loading}
            >
              Search
            </button>
          </div>
        </motion.div>
      )}

      {snippets.length > 0 && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="card p-3 my-4">
          <h3 className="text-xl font-semibold mb-3">Top Snippets</h3>
          <button
            className="btn btn-secondary mb-3"
            onClick={handleRefineOrder}
            disabled={loading}
          >
            Refine Order with AI
          </button>

          <SnippetEditor
            snippets={snippets}
            onCreateVideo={handleCreateVideo}
            loading={loading}
          />
        </motion.div>
      )}

      {videoPath && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="card p-3 my-4">
          <h4 className="text-lg font-semibold mb-3">Video Created Successfully!</h4>
          <p>Download your highlight video:</p>
          <a
            href={`http://localhost:5001/api/download-video/${videoPath.split('/').pop()}`}
            className="btn btn-success"
            target="_blank"
            rel="noreferrer"
          >
            Download/Play Video
          </a>
        </motion.div>
      )}
    </div>
  );
}

export default App;
