import React, { useState, useRef, useEffect } from "react";
import VideoSearchForm from "./components/VideoSearchForm";
import SnippetEditor from "./components/SnippetEditor";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./App.css"; // Custom styling

function App() {
  const [youtubeUrl, setYoutubeUrl] = useState("");
  const [chunks, setChunks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [snippets, setSnippets] = useState([]);
  const [videoPath, setVideoPath] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Refs for auto-focus
  const searchInputRef = useRef(null);
  const refineButtonRef = useRef(null);
  const createVideoButtonRef = useRef(null);
  const downloadButtonRef = useRef(null);

  // Full-screen loading overlay
  const LoadingOverlay = () => (
    <div className="loading-overlay">
      <div className="loading-spinner">
        <div className="spinner-border text-light" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p>Processing your request...</p>
      </div>
    </div>
  );

  const handleFetchChunks = async (url) => {
    try {
      setYoutubeUrl(url);
      setLoading(true);
      setError(null);
      setSnippets([]);
      setVideoPath(null);

      const response = await axios.post("http://localhost:5001/api/transcript-chunks", {
        youtubeUrl: url,
        maxChunkSize: 200,
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        setChunks(response.data.chunks);
        toast.success("Transcript fetched successfully!");

        // Auto-focus on search input field
        setTimeout(() => searchInputRef.current?.focus(), 300);
      }
    } catch (err) {
      toast.error("Failed to fetch transcript.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (query, topK = 5) => {
    if (!chunks.length) return;
    try {
      setSearchQuery(query);
      setLoading(true);
      setError(null);
      setVideoPath(null);

      const resp = await axios.post("http://localhost:5001/api/search-snippets", {
        chunks: chunks,
        query: query,
        topK: topK,
      });

      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setSnippets(resp.data.results);
        toast.success("Snippets found!");

        // Auto-focus on refine button
        setTimeout(() => refineButtonRef.current?.focus(), 300);
      }
    } catch (err) {
      toast.error("Snippet search failed.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleRefineOrder = async () => {
    if (!snippets.length) return;
    try {
      setLoading(true);
      setError(null);

      const resp = await axios.post("http://localhost:5001/api/refine-snippets", {
        snippets: snippets,
        query: searchQuery,
      });

      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setSnippets(resp.data.order.map((idx) => snippets[idx]));
        toast.info("Snippets reordered.");

        // Auto-focus on create video button
        setTimeout(() => createVideoButtonRef.current?.focus(), 300);
      }
    } catch (err) {
      toast.error("Refining failed.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateVideo = async (finalSnippets) => {
    try {
      setLoading(true);
      setError(null);
      setVideoPath(null);

      const resp = await axios.post("http://localhost:5001/api/create-video", {
        youtubeUrl: youtubeUrl,
        snippets: finalSnippets,
      });

      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setVideoPath(resp.data.videoPath);
        toast.success("Video created!");

        // Auto-focus on download button
        setTimeout(() => downloadButtonRef.current?.focus(), 300);
      }
    } catch (err) {
      toast.error("Video creation failed.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container my-4">
      {loading && <LoadingOverlay />} {/* Full-screen loading */}

      <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />
      <h1 className="text-center gradient-text">🎬 AI Video Editor</h1>

      <VideoSearchForm onFetchChunks={handleFetchChunks} loading={loading} />

      {error && <div className="alert alert-danger my-3">{error}</div>}

      {chunks.length > 0 && (
        <div className="card my-4 p-3 animated-section">
          <h3>Step 2: Search in Transcript</h3>
          <input
            ref={searchInputRef}
            type="text"
            className="form-control"
            placeholder="e.g. 'social media impact'"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button
            className="btn btn-primary mt-2"
            onClick={() => handleSearch(searchQuery, 5)}
            disabled={!searchQuery || loading}
          >
            Search
          </button>
        </div>
      )}

      {snippets.length > 0 && (
        <div className="card my-4 p-3 fade-in">
          <h3>Step 3: Refine Snippets</h3>
          <button
            ref={refineButtonRef}
            className="btn btn-secondary mt-3"
            onClick={handleRefineOrder}
            disabled={loading}
          >
            Refine/Reorder
          </button>

          <SnippetEditor
            snippets={snippets}
            onCreateVideo={handleCreateVideo}
            loading={loading}
            createVideoRef={createVideoButtonRef}
          />
        </div>
      )}

      {videoPath && (
        <div className="card p-3 my-4">
          <h4>Video Created!</h4>
          <a
            ref={downloadButtonRef}
            href={`http://localhost:5001/api/download-video/${videoPath.split("/").pop()}`}
            className="btn btn-success"
            target="_blank"
            rel="noreferrer"
          >
            Download Video
          </a>
        </div>
      )}
    </div>
  );
}

export default App;
