import React, { useState, useRef, useEffect } from 'react';
import VideoSearchForm from './components/VideoSearchForm';
import SnippetEditor from './components/SnippetEditor';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

// A simple full-screen overlay spinner.
function LoadingOverlay() {
  return (
    <div className="loading-overlay">
      <div className="spinner-border text-light" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    </div>
  );
}

function App() {
  const [youtubeUrl, setYoutubeUrl] = useState('');
  const [chunks, setChunks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [snippets, setSnippets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [videoPath, setVideoPath] = useState(null);
  const [error, setError] = useState(null);

  // Track the current step to guide the user through the flow.
  // Steps:
  // 1 - Fetch Transcript
  // 2 - Search Transcript
  // 3 - Refine Snippets
  // 4 - Create Video
  // 5 - Download
  const [currentStep, setCurrentStep] = useState(1);

  // Refs for auto-focusing the next action button/section.
  const searchButtonRef = useRef(null);
  const refineButtonRef = useRef(null);
  const createVideoButtonRef = useRef(null);
  const downloadButtonRef = useRef(null);

  // Whenever the current step changes AND we're not loading, focus the appropriate element.
  useEffect(() => {
    if (!loading) {
      switch (currentStep) {
        case 2:
          searchButtonRef.current?.focus();
          break;
        case 3:
          refineButtonRef.current?.focus();
          break;
        case 4:
          createVideoButtonRef.current?.focus();
          break;
        case 5:
          downloadButtonRef.current?.focus();
          break;
        default:
          break;
      }
    }
  }, [currentStep, loading]);

  const handleFetchChunks = async (url) => {
    try {
      setYoutubeUrl(url);
      setLoading(true);
      setError(null);
      setSnippets([]);
      setVideoPath(null);

      const response = await axios.post('http://localhost:5001/api/transcript-chunks', {
        youtubeUrl: url,
        maxChunkSize: 200,
      });
      if (response.data.error) {
        setError(response.data.error);
      } else {
        setChunks(response.data.chunks);
        toast.success('Transcript chunks fetched successfully!');
        setCurrentStep(2); // Move to Step 2: Search Transcript
      }
    } catch (err) {
      toast.error('Failed to fetch transcript chunks.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (query, topK = 5) => {
    if (!chunks.length) return;
    try {
      setSearchQuery(query);
      setLoading(true);
      setError(null);
      setVideoPath(null);

      const resp = await axios.post('http://localhost:5001/api/search-snippets', {
        chunks: chunks,
        query: query,
        topK: topK,
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setSnippets(resp.data.results);
        toast.success('Top snippets found!');
        setCurrentStep(3); // Move to Step 3: Refine Snippets
      }
    } catch (err) {
      toast.error('Snippet search failed.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleRefineOrder = async () => {
    if (!snippets.length) return;
    try {
      setLoading(true);
      setError(null);

      const resp = await axios.post('http://localhost:5001/api/refine-snippets', {
        snippets: snippets,
        query: searchQuery,
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        const newOrder = resp.data.order;
        const reordered = newOrder.map((idx) => snippets[idx]).filter(Boolean);
        setSnippets(reordered);
        toast.info('Snippets refined and reordered.');
        setCurrentStep(4); // Move to Step 4: Create Video
      }
    } catch (err) {
      toast.error('Refining order failed.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateVideo = async (finalSnippets) => {
    try {
      setLoading(true);
      setError(null);
      setVideoPath(null);
      const resp = await axios.post('http://localhost:5001/api/create-video', {
        youtubeUrl: youtubeUrl,
        snippets: finalSnippets,
      });
      if (resp.data.error) {
        setError(resp.data.error);
      } else {
        setVideoPath(resp.data.videoPath);
        toast.success('Highlight video created!');
        setCurrentStep(5); // Move to Step 5: Download
      }
    } catch (err) {
      toast.error('Video creation failed.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Helper: Determine whether a step is accessible.
  // For simplicity, allow navigation to any step that is less than or equal to currentStep.
  const isStepAccessible = (step) => step <= currentStep;

  return (
    <div className="container py-4 position-relative">
      {loading && <LoadingOverlay />}

      <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />

      {/* Tab Navigation Header */}
      <ul className="nav nav-tabs">
        <li className="nav-item">
          <button
            className={`nav-link ${currentStep === 1 ? 'active' : ''}`}
            onClick={() => setCurrentStep(1)}
          >
            1. Fetch Transcript
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${currentStep === 2 ? 'active' : ''}`}
            onClick={() => isStepAccessible(2) && setCurrentStep(2)}
            disabled={!isStepAccessible(2)}
          >
            2. Search Transcript
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${currentStep === 3 ? 'active' : ''}`}
            onClick={() => isStepAccessible(3) && setCurrentStep(3)}
            disabled={!isStepAccessible(3)}
          >
            3. Refine Snippets
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${currentStep === 4 ? 'active' : ''}`}
            onClick={() => isStepAccessible(4) && setCurrentStep(4)}
            disabled={!isStepAccessible(4)}
          >
            4. Create Video
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${currentStep === 5 ? 'active' : ''}`}
            onClick={() => isStepAccessible(5) && setCurrentStep(5)}
            disabled={!isStepAccessible(5)}
          >
            5. Download
          </button>
        </li>
      </ul>

      {/* Tab Content */}
      <div className="tab-content mt-4">
        {currentStep === 1 && (
          <div className="tab-pane active">
            <VideoSearchForm onFetchChunks={handleFetchChunks} loading={loading} />
          </div>
        )}

        {currentStep === 2 && (
          <div className="tab-pane active">
            <div className="card my-4 p-3">
              <h3>Step 2: Search in Transcript</h3>
              <p>Enter a query to find relevant parts:</p>
              <div className="input-group mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="e.g. 'social media impact'"
                  onChange={(e) => setSearchQuery(e.target.value)}
                  value={searchQuery}
                />
                <button
                  className="btn btn-primary"
                  ref={searchButtonRef}
                  onClick={() => handleSearch(searchQuery, 5)}
                  disabled={!searchQuery || loading}
                >
                  Search
                </button>
              </div>
            </div>
          </div>
        )}

        {currentStep === 3 && (
          <div className="tab-pane active">
            <div className="card my-4 p-3">
              <h3>Step 3: Refine Snippets</h3>
              <p>
                Review the snippets below and click "Refine / Reorder" to improve the order.
              </p>
              <ul>
                {snippets.map((snippet, index) => (
                  <li key={index}>{snippet.text}</li>
                ))}
              </ul>
              <button
                className="btn btn-secondary mt-3"
                ref={refineButtonRef}
                onClick={handleRefineOrder}
                disabled={loading}
              >
                Refine / Reorder
              </button>
            </div>
          </div>
        )}

        {currentStep === 4 && (
          <div className="tab-pane active">
            <div className="card my-4 p-3">
              <h3>Step 4: Create Video</h3>
              <SnippetEditor
                snippets={snippets}
                onCreateVideo={handleCreateVideo}
                loading={loading}
                createVideoButtonRef={createVideoButtonRef}
              />
            </div>
          </div>
        )}

        {currentStep === 5 && videoPath && (
          <div className="tab-pane active">
            <div className="card p-3 my-4">
              <h4>Highlight Video Created!</h4>
              <p>Download or play your highlight video:</p>
              <a
                href={`http://localhost:5001/api/download-video/${videoPath.split('/').pop()}`}
                className="btn btn-success"
                target="_blank"
                rel="noreferrer"
                ref={downloadButtonRef}
              >
                Download / Play
              </a>
            </div>
          </div>
        )}
      </div>

      {/* Error Alert */}
      {error && <div className="alert alert-danger my-3">{error}</div>}
    </div>
  );
}

export default App;
